# Model Evaluation Functions\n\n## Load necessary libraries\nlibrary(pROC)\nlibrary(caret)\n\n## Function to calculate ROC curve and AUC\nroc_auc <- function(truth, predicted) {\n  roc_obj <- roc(truth, predicted)\n  auc_val <- auc(roc_obj)\n  list(roc_obj = roc_obj, auc = auc_val)\n}\n\n## Function to calculate sensitivity and specificity\nsensitivity_specificity <- function(conf_matrix) {\n  sensitivity <- conf_matrix["positive","predicted"] / sum(conf_matrix[,"predicted"] == "positive")\n  specificity <- conf_matrix["negative","predicted"] / sum(conf_matrix[,"predicted"] == "negative")\n  list(sensitivity = sensitivity, specificity = specificity)\n}\n\n## Function to create confusion matrix\ncreate_confusion_matrix <- function(truth, predicted) {\n  confusionMatrix(factor(predicted), factor(truth))\n}\n